sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var retire_PRL = current.retire_prl;
	var pre_build_complete = current.pre_build_complete;
	var non_med_build_complete = current.non_med_build_complete;
	var clinical_greenlight = current.clinical_greenlight;
	var clinical_nursing_greenlight = current.clinical_nursing_greenlight;
	var med_nursing_greenlight = current.medication_nursing_greenlight;
	var rx_greenlight = current.medication_pharmacy_greenlight;
	var type = current.version_type;
	var status = '';

	if(clinical_greenlight == true && clinical_nursing_greenlight == true && med_nursing_greenlight)
		status = '7';
	else if(type == '10' && retire_PRL == true)
		status = '7';
	else if(type == '10' && retire_PRL == false)
		status = '5';
	else if(type == '9')
		status = '6';
	else if(pre_build_complete == false)
		status = '2';
	else if (non_med_build_complete == false)
		status = '3';
	else if(non_med_build_complete == true && clinical_greenlight == false)
		status = '1';
	else if(clinical_greenlight == true)
		status = '4';


current.setValue('u_non_med_status', status);
current.update();


})(current, previous);
