sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var assignKey = current.assignment_key.toString();
    var identifier = current.linked_version;
    var researchGlide = new GlideRecord('x_sthc_research_version');
    researchGlide.get(identifier);

    switch (assignKey) {
        case 'Medication Nursing Review':
            researchGlide.setValue('medication_nursing_greenlight', true);
            break;
        case 'Medication Build Validation':
            researchGlide.setValue('medication_pharmacy_greenlight', true);
            break;
        case 'Non-Med Build Validation':
            researchGlide.setValue('clinical_greenlight', true);
            break;
        case 'Non-Med Build Nursing Review':
            researchGlide.setValue('clinical_nursing_greenlight', true);
            break;
        case 'prebuild':
            researchGlide.setValue('pre_build_complete', true);
            break;
        case 'Medication Pre-Build':
            researchGlide.setValue('pre_build_rx_complete', true);
            break;
        case 'Migration to production':
            researchGlide.setValue('status', '4');
            researchGlide.setValue('active', false);
            break;
        case 'Retire PRL':
            researchGlide.setValue('retire_prl', true);
            break;
        default:
            gs.log(current.number + ' has been closed, but did not meet any criteria in BR Validation Complete');

    }

    researchGlide.update();

})(current, previous);
