sys_script_client.script:
function onLoad() {

	var req = g_form.getReference('linked_version',callback);

}

function callback(req){
	var Akey = g_form.getValue('assignment_key').toString();

	switch(Akey){
		case 'Non-Med Build Validation':
			if(req.non_med_revisions_nursing == 'true')
				g_form.showFieldMsg('state','Nursing has aleady flagged this as Revisions Needed');
			if(req.clinical_nursing_greenlight == 'true')
				g_form.showFieldMsg('state','Nursing has already flagged this as Complete - Approved');
			break;
		case 'Non-Med Build Nursing Review':
			if(req.non_med_revisions == 'true')
				g_form.showFieldMsg('state','Research team has already flagged this as Revisions Needed');
			if(req.clinical_greenlight == 'true')
				g_form.showFieldMsg('state','Research team has already flagged this as Complete - Approved');
			break;
		case 'Medication Build Validation':
			if(req.medications_revisions_nursing == 'true')
				g_form.showFieldMsg('state','Nursing has already flagged medications as Revisions Needed');
			if(req.medication_nursing_greenlight == 'true')
				g_form.showFieldMsg('state','Nursing has already flagged medications as Complete - Approved');
			break;
		case 'Medication Nursing Review':
			if(req.medications_revisions == 'true')
				g_form.showFieldMsg('state','Pharmacy has already flagged medications as Revisions Needed');
			if(req.medication_pharmacy_greenlight == 'true')
				g_form.showFieldMsg('state','Pharmacy has already flagged medications as Complete - Approved');
			break;
	}

}
