sys_script_include.script:
var ResearchUtil = Class.create();
ResearchUtil.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getStudyName: function(){
		var armID = this.getParameter('sysparm_arm');

		var gr = new GlideRecord('x_sthc_research_study_arm');
			gr.get(armID);
		var obj = {};
		obj.armName = gr.name.getDisplayValue();
		obj.studyName = gr.linked_study.getDisplayValue();


		var json = new global.JSON();
		var answer = json.encode(obj);

		return answer;
	},

    type: 'ResearchUtil'
});
